var document=this;var subject="";function changeSubject(sub){"use strict";switch(sub){case"services":document.getElementById("services_TypeAndDate").style.display="";document.getElementById("other_Info").style.display="none";subject="Services";break;case"questionsComments":document.getElementById("services_TypeAndDate").style.display="none";document.getElementById("other_Info").style.display="none";subject="Questions/Comments";break;case"other":document.getElementById("services_TypeAndDate").style.display="none";document.getElementById("other_Info").style.display="";subject="Other";break;default:document.getElementById("services_TypeAndDate").style.display="none";document.getElementById("other_Info").style.display="none";subject="";}}
function formatSubject(){"use strict";var extraInfo="";if(subject==="Services"){extraInfo=" - "+document.getElementById("serviceType").value+" on "+document.getElementById("serviceDate").value;}else if(subject==="Other"){extraInfo=" - "+document.getElementById("otherSubject").value;}
document.getElementById("subject").value=subject+extraInfo;}
